package week1.day2;

public class IsPrimeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n=1;
int p;
for(int i=n;i<=10;i++)
{
	if(i/n==0)
	
	{
		System.out.println("Not Prime" +p);
	}
	else
	{
		System.out.println("Prime Number" +p);
	}
}
	}}



